const yamlTemplate = `
article:
  head: |
    \\documentclass{article}

    % jats2tex requirements
    @@requirements

    \\begin{document}

    @@heads

    \\maketitle

    @@bodies

    \\end{document}

article-title:
  head: |
    \\title{@@children}

contrib: "@@children"

back: ""

name: |
  @@lua(
  s = find "surname"
  g = find "given-names"
  return s .. ", " .. g
  )@@

contrib-group:
  head: |

    \\author{@@lua(
    return table.concat(elements(), '\\\\and ')
    )@@}

break: "\\n"

code: |
  \\code{@@children}

codebold: |
  \\code{@@children}

kwd: |
  \\textit{@@children}

p: |

  @@lua(
    align_to_ragged = {
      ["center"] = "Center",
      ["left"] = "FlushLeft",
      ["right"] = "FlushRight",
    }

    if attr('align') == nil then
      return children()
    end

    env = align_to_ragged[attr('align')]
    if env == nil then
      return children()
    end

    return string.format([[
      \\begin{%s}
      %s
      \\env{%s}
    ]], env, env, children())
  )@@

pub-date: |
  \\date{@@lua(return find "year")@@}{@@lua(return find "month")@@}{@@lua(return find "day")@@}

abstract: |
  @@lua(
    if attr('lang') == 'en' then
      return "\\abstract{" .. children() .. "}"
    end

    return ""
  )@@

volume: |
  \\newcommand{\\volume}{@@children}

numero: |
  \\newcommand{\\numero}{@@children}

fpage: |
  \\newcommand{\\fpage}{@@children}

lpage: |
  \\newcommand{\\lpage}{@@children}

copyright-statement: |
  \\newcommand{\\copyrightstatement}{@@children}

copyright-year: |
  \\newcommand{\\copyrightyear}{@@children}

b: |
  \\textbf{@@children}
`;

import * as querystring from 'querystring';
import * as React from 'react';
import {Component} from 'react';

import * as SplitPane from 'react-split-pane';
import {paneStyle, pane1Style, pane2Style} from 'react-split-pane';
import 'codemirror';
import 'codemirror/lib/codemirror.css';
import 'codemirror/mode/lua/lua';
import 'codemirror/mode/yaml/yaml';
import 'codemirror/mode/xml/xml';
import 'codemirror/mode/stex/stex';
import * as Codemirror from 'react-codemirror';
import './app.css';

class SourceEditor extends Component {
  render() {
    const options = {
      lineNumbers: true,
      mode: this.props.mode,
    };
    return <Codemirror options={options} {...this.props} />;
  }
}

class PreviewPdf extends Component {
  state = {
    url: null,
  };

  componentDidUpdate(prevProps) {
    if (this.props.value && this.props.value !== prevProps.value) {
      fetch('http://localhost:3001/', {
        method: 'post',
        headers: {
          'content-type': 'application/x-www-form-urlencoded',
        },
        mode: 'cors',
        body: querystring.stringify({
          text: this.props.value,
        }),
      }).then(res => {
        this.setState({
          url: res.url,
        });
      });
    }
  }

  renderNoPreview() {
    return (
      <div
        style={{
          display: 'flex',
          height: '100%',
          width: '100%',
          alignItems: 'center',
          justifyContent: 'center',
        }}
      >
        Preview not available yet
      </div>
    );
  }

  render() {
    if (this.props.value && this.state.url) {
      return (
        <iframe
          style={{height: '100%', width: '100%'}}
          frameborder="0"
          src={this.state.url}
        />
      );
    }

    if (this.props.value && !this.state.url) {
      return <div />;
    }
    return (
      <div style={{height: '100%', width: '100%'}}>
        {this.renderNoPreview()}
      </div>
    );
  }
}

export default class App extends Component {
  state = {
    conversionResult: '',
    template: '',
    text: xmlExample,
    yamlTemplate,
  };

  componentDidMount() {
    this.runConvert();
  }

  runConvert = () => {
    fetch('/', {
      method: 'post',
      headers: {
        'content-type': 'application/x-www-form-urlencoded',
      },
      body: querystring.stringify({
        text: this.state.text,
      }),
    })
      .then(res => res.text())
      .then(
        t => {
          console.log(t);
          this.setState({conversionResult: t});
        },
        err => {
          console.error(err);
          alert('Error converting.' + err);
        },
      );
  };

  render() {
    return (
      <div>
        <div
          style={{
            position: 'relative',
            height: 'calc(100vh - 98px)',
            width: '100vw',
            marginTop: '-98px',
            marginBottom: '98px',
            paddingTop: '77px',
          }}
        >
          <SplitPane
            split="vertical"
            defaultSize={document.body.offsetWidth / 2}
            minSize={0.2 * document.body.offsetWidth}
          >
            <div style={{...paneStyle, ...pane1Style}}>
              <SplitPane
                split="horizontal"
                defaultSize={document.body.offsetHeight * 0.45}
                minSize={0.2}
              >
                <div style={{...paneStyle, ...pane1Style}}>
                  <SourceEditor
                    mode="xml"
                    defaultValue={this.state.text}
                    onChange={e => this.setState(e.target.value)}
                  />
                </div>
                <div style={{...paneStyle, ...pane2Style}}>
                  <SourceEditor
                    mode="yaml"
                    defaultValue={this.state.yamlTemplate}
                  />
                </div>
              </SplitPane>
            </div>

            <div style={{...paneStyle, ...pane2Style}}>
              <SplitPane
                split="horizontal"
                defaultSize={document.body.offsetHeight * 0.45}
                minSize={0.2}
              >
                <div style={{...paneStyle, ...pane1Style}}>
                  <SourceEditor
                    mode="stex"
                    key={this.state.conversionResult}
                    autoSave
                    value={this.state.conversionResult}
                  />
                </div>
                <div style={{...paneStyle, ...pane2Style}}>
                  <PreviewPdf value={this.state.conversionResult} />
                </div>
              </SplitPane>
            </div>
          </SplitPane>
        </div>
        <hr />

        <button
          style={{position: 'absolute', top: 98, right: 15}}
          className="btn btn-primary"
          onClick={this.runConvert}
        >
          Run jats2tex
        </button>
      </div>
    );
  }
}
